INSERT INTO `movies` (`id`, `name`, `video`, `video_describe`, `photo`, `author_id`) VALUES
(1, 'Життя Тараса Шевченка (1951)', 'https://www.youtube.com/embed/spp7UWrzGXo', 'Біографічний фільм про життя і творчість видатного українського поета і художника Тараса Григоровича Шевченка. Син кріпака, він блискуче закінчив Академію мистецтв і ще в юності завоював славу волелюбними віршами.', '7069.jpg', 1),
(13, 'Машина Часу', 'https://www.youtube.com/embed/m-ZQxBT61kI', 'Перша серія з циклу програм про І. Франка. Як Кремль фінансував проросійські рухи в Галичині; що примусило емігрувати Драгоманова з Києва і як Франко вперше опинився за ґратами', 'й1234.jpg', 6),
(12, 'Таємниці Генія Шевченка', 'https://www.youtube.com/embed/DvBAn63jkH0', 'Вчителям, учням, студентам та всім, хто знає, що за заїждженим образом Тарас Шевченко приховується жива людина - зі своїми радостями, проблемами і таємницями.\r\nТаємниці Генія Шевченка.', 'Без названия (4).jpg', 1),
(14, 'Наруто и Киллер Би', 'https://www.youtube.com/embed/Pgpf9UxaFmQ', '\"Если попадёшься в Аматересу тебе конец, Мангеке Шаринган\"/ Наруто и Киллер Би против Итачи и Нагато. Но ведь против аматерасу можно использовать покров хвостатого, как это сделал нарик против саске в финальной битве', '220px-Облкладинка_«Naruto»_(танкобон).jpg', 2);

