INSERT INTO `poems` (`id`, `name`, `text`, `author_id`) VALUES
(2, 'Заповіт', 'Як умру, то поховайте\r\nМене на могилі,\r\nСеред степу широкого,\r\nНа Вкраїні милій,\r\nЩоб лани широкополі,\r\nІ Дніпро, і кручі\r\nБуло видно, було чути,\r\nЯк реве ревучий.\r\nЯк понесе з України\r\nУ синєє море\r\nКров ворожу... отойді я\r\nІ лани, і гори —\r\nВсе покину і полину\r\nДо самого бога\r\nМолитися... а до того\r\nЯ не знаю бога.\r\nПоховайте та вставайте,\r\nКайдани порвіте\r\nІ вражою злою кров\'ю\r\nВолю окропіте.\r\nІ мене в сем\'ї великій,\r\nВ сем\'ї вольній, новій,\r\nНе забудьте пом\'янути\r\nНезлим тихим словом.', 1),
(8, 'І мертвим, і живим, і ненарожденним...', 'І смеркає, і світає,\r\nДень божий минає,\r\nІ знову люд потомлений,\r\nІ все спочиває.\r\nТілько я, мов окаянний,\r\nІ день і ніч плачу\r\nНа розпуттях велелюдних,\r\nІ ніхто не бачить,\r\nІ не бачить, і не знає –\r\nОглухли, не чують;\r\nКайданами міняються,\r\nПравдою торгують.\r\nІ Господа зневажають,\r\nЛюдей запрягають\r\nВ тяжкі ярма. Орють лихо,\r\nЛихом засівають,\r\nА що вродить? побачите,\r\nЯкі будуть жни́ва!\r\nСхаменіться, недолюди,\r\nДіти юродиві!\r\nПодивіться на рай тихий,\r\nНа свою країну,\r\nПолюбіте щирим серцем\r\nВелику руїну,\r\nРозкуйтеся, братайтеся,\r\nУ чужому краю\r\nНе шукайте, не питайте\r\nТого, що немає\r\nІ на небі, а не тілько\r\nНа чужому полі.\r\nВ своїй хаті своя й правда,\r\nІ сила, і воля.\r\nНема на світі України,\r\nНемає другого Дніпра,\r\nА ви претеся на чужину\r\nШукати доброго добра,\r\nДобра святого. Волі! волі!\r\nБратерства братнього! Найшли,\r\nНесли, несли з чужого поля\r\nІ в Україну принесли\r\nВеликих слов велику силу,\r\nТа й більш нічого. Кричите,\r\nЩо Бог создав вас не на те,\r\nЩоб ви неправді поклонились!..\r\nІ хилитесь, як і хилились!\r\nІ знову шкуру дерете\r\nЗ братів незрящих, гречкосіїв,\r\nІ сонця-правди дозрівать\r\nВ німецькі землі, не чужії,\r\nПретеся знову!.. Якби взять\r\nІ всю мізерію з собою,\r\nДідами крадене добро,\r\nТойді оставсь би сиротою\r\nЗ святими горами Дніпро!', 1),
(7, 'Думи мої думи…', 'Думи мої, думи мої,\r\nЛихо мені з вами!\r\nНащо стали на папері\r\nСумними рядами?..\r\nЧом вас вітер не розвіяв\r\nВ степу, як пилину?\r\nЧом вас лихо не приспало,\r\nЯк свою дитину?...\r\nЗа карії оченята,\r\nЗа чорнії брови\r\nСерце рвалося, сміялось,\r\nВиливало мову,\r\nВиливало, як уміло,\r\nЗа темнії ночі,\r\nЗа вишневий сад зелений,\r\nЗа ласки дівочі...\r\nЗа степи та за могили,\r\nЩо на Україні,\r\nСерце мліло, не хотіло\r\nСпівать на чужині...\r\nНе хотілось в снігу, в лісі,\r\nКозацьку громаду\r\nЗ булавами, з бунчугами\r\nЗбирать на пораду.\r\nНехай душі козацькії\r\nВ Украйні витають –\r\nТам широко, там весело\r\nОд краю до краю...\r\nЯк та воля, що минулась,\r\nДніпр широкий – море,\r\nСтеп і степ, ревуть пороги,\r\nІ могили – гори, –\r\nТам родилась, гарцювала\r\nКозацькая воля;\r\nТам шляхтою, татарами\r\nЗасідала поле,\r\nЗасівала трупом поле,\r\nПоки не остило...', 1),
(10, 'Серце мліло, не хотіло Співать на чужині...', 'Не хотілось в снігу, в лісі,\r\nКозацьку громаду\r\nЗ булавами, з бунчугами\r\nЗбирать на пораду.\r\nНехай душі козацькії\r\nВ Украйні витають –\r\nТам широко, там весело\r\nОд краю до краю...\r\nЯк та воля, що минулась,\r\nДніпр широкий – море,\r\nСтеп і степ, ревуть пороги,\r\nІ могили – гори, –\r\nТам родилась, гарцювала\r\nКозацькая воля;\r\nТам шляхтою, татарами\r\nЗасідала поле,\r\nЗасівала трупом поле,\r\nПоки не остило...', 0),
(9, 'Сон', 'У всякого своя доля\r\nІ свій шлях широкий:\r\nТой мурує, той руйнує,\r\nТой неситим оком\r\nЗа край світа зазирає,—\r\nЧи нема країни,\r\nЩоб загарбать і з собою\r\nВзять у домовину.\r\nТой тузами обирає\r\nСвата в його хаті,\r\nА той нишком у куточку\r\nГострить ніж на брата.\r\nА той, тихий та тверезий,\r\nБогобоязливий,\r\nЯк кішечка, підкрадеться,\r\nВижде нещасливий\r\nУ тебе час та й запустить\r\nПазурі в печінки,—\r\nІ не благай: не вимолять\r\nНі діти, ні жінка.\r\nА той, щедрий та розкошний,\r\nВсе храми мурує;\r\nТа отечество так любить,\r\nТак за ним бідкує,\r\nТак із його, сердешного,\r\nКров, як воду, точить!..\r\nА братія мовчить собі,\r\nВитріщивши очі!\r\nЯк ягнята; «Нехай,— каже,—\r\nМоже, так і треба».\r\nТак і треба! бо немає\r\nГоспода на небі!\r\nА ви в ярмі падаєте\r\nТа якогось раю\r\nНа тім світі благаєте?\r\nНемає! немає!\r\nШкода й праці. Схаменіться:\r\nУсі на сім світі —\r\nІ царята і старчата —\r\nАдамові діти.\r\nІ той... і той... а що ж то я?!\r\nОсь що, добрі люди:\r\nЯ гуляю, бенкетую\r\nВ неділю і в будень.\r\nА вам нудно! жалкуєте!\r\nЄй-богу, не чую,\r\nІ не кричіть! Я свою п\\\'ю,\r\nА не кров людськую!\r\n\r\nОтак, ідучи попідтинню\r\nЗ бенкету п\\\'яний уночі,\r\nЯ міркував собі йдучи,\r\nПоки доплентавсь до хатини.\r\nА в мене діти не кричать\r\nІ жінка не лає,\r\nТихо, як у раї,\r\nУсюди божа благодать —\r\nІ в серці, і в хаті.\r\nОтож я ліг спати.\r\nА вже підпилий як засне,\r\nТо хоч коти гармати,\r\nІ усом не моргне.\r\nТа й сон же, сон, напричуд дивний,\r\nМені приснився —\r\nНайтверезіший би упився,\r\nСкупий жидюга дав би гривню,\r\nЩоб позирнуть на ті дива.\r\nТа чорта з два!\r\nДивлюся: так буцім сова\r\nЛетить лугами, берегами, та нетрями,\r\nТа глибокими ярами,\r\nТа широкими степами,\r\nТа байраками.\r\nА я за нею, та за нею,\r\nЛечу й прощаюся з землею:\r\n«Прощай, світе, прощай, земле,\r\nНеприязний краю,\r\nМої муки, мої люті\r\nВ хмарі заховаю.\r\nА ти, моя Україно,\r\nБезталанна вдово,\r\nЯ до тебе літатиму\r\nЗ хмари на розмову.\r\nНа розмову тихо-сумну,\r\nНа раду з тобою;\r\nОпівночі падатиму\r\nРясною росою.\r\nПорадимось, посумуєм,\r\nПоки сонце встане;\r\nПоки твої малі діти\r\nНа ворога стануть.\r\nПрощай же ти, моя нене,\r\nУдово небого,\r\nГодуй діток; жива правда\r\nУ господа бога!»\r\n\r\nЛетим. Дивлюся, аж світає,\r\nКрай неба палає,\r\nСоловейко в темнім гаї\r\nСонце зустрічає.\r\nТихесенько вітер віє,\r\nСтепи, лани мріють,\r\nМеж ярами над ставами\r\nВерби зеленіють.\r\nСади рясні похилились,\r\nТополі по волі\r\nСтоять собі, мов сторожа,\r\nРозмовляють з полем.\r\nІ все то те, вся країна,\r\nПовита красою,\r\nЗеленіє, вмивається\r\nДрібною росою,\r\nСпоконвіку вмивається,\r\nСонце зустрічає...\r\nІ нема тому почину,\r\nІ краю немає!\r\nНіхто його не додбає\r\nІ не розруйнує...\r\nІ все то те... Душе моя,\r\nЧого ти сумуєш?\r\nДуше моя убогая,\r\nЧого марне плачеш,\r\nЧого тобі шкода? хіба ти не бачиш,\r\nХіба ти не чуєш людського плачу?\r\nТо глянь, подивися; а я полечу\r\nВисоко, високо за синії хмари;\r\nНемає там власті, немає там кари,\r\nТам сміху людського і плачу не чуть.\r\nОн глянь,— у тім раї, що ти покидаєш,\r\nЛатану свитину з каліки знімають,\r\nЗ шкурою знімають, бо нічим обуть\r\nКняжат недорослих; а он розпинають\r\nВдову за подушне, а сина кують,\r\nЄдиного сина, єдину дитину,\r\nЄдину надію! в військо оддають!\r\nБо його, бач, трохи! а онде під тином\r\nОпухла дитина, голоднеє мре,\r\nА мати пшеницю на панщині жне.\r\nА он бачиш? очі! очі!\r\nНащо ви здалися,\r\nЧом ви змалку не висохли,\r\nСлізьми не злилися?\r\nТо покритка, попідтинню\r\nЗ байстрям шкандибає,\r\nБатько й мати одцурались,\r\nЙ чужі не приймають!\r\nСтарці навіть цураються!!\r\nА панич не знає,\r\nЗ двадцятою, недоліток,\r\nДуші пропиває!\r\nЧи бог бачить із-за хмари\r\nНаші сльози, горе?\r\nМоже, й бачить, та помага,\r\nЯк і оті гори\r\nПредковічні, що политі\r\nКровію людською!..\r\nДуше моя убогая!\r\nЛишенько з тобою.\r\nУп\\\'ємося отрутою,\r\nВ кризі ляжем спати,\r\nПошлем думу аж до бога:\r\nЙого розпитати,\r\nЧи довго ще на сім світі\r\nКатам панувати??\r\n\r\nЛети ж, моя думо, моя люта муко,\r\nЗабери з собою всі лиха, всі зла,\r\nСвоє товариство — ти з ними росла,\r\nТи з ними кохалась, їх тяжкії руки\r\nТебе повивали. Бери ж їх, лети\r\nТа по всьому небу орду розпусти.\r\nНехай чорніє, червоніє,\r\nПолум\\\'ям повіє,\r\nНехай знову рига змії,\r\nТрупом землю криє.\r\nА без тебе я де-небудь\r\nСерце заховаю\r\nТа тим часом пошукаю\r\nНа край світа раю.\r\nІ знов лечу понад землею,\r\nІ знов прощаюся я з нею.\r\nТяжко матір покидати\r\nУ безверхій хаті.\r\nА ще гірше дивитися\r\nНа сльози та лати.\r\n\r\nЛечу, лечу, а вітер віє,\r\nПередо мною сніг біліє,\r\nКругам бори та болота,\r\nТуман, туман і пустота.\r\nЛюдей не чуть, не знать і сліду\r\nЛюдської страшної ноги.\r\nІ вороги й не вороги,\r\nПрощайте, в гості не приїду!\r\nУпивайтесь, бенкетуйте —\r\nЯ вже не почую,\r\nОдин собі навік-віки\r\nВ снігу заночую.\r\nІ поки ви дознаєтесь,\r\nЩо ще є країна,\r\nНе полита сльозьми, кров\\\'ю,\r\nТо я одпочину.\r\nОдпочину... аж слухаю —\r\nЗагули кайдани\r\nПід землею... Подивлюся...\r\nО люде поганий!\r\nДе ти взявся? що ти робиш?\r\nЧого ти шукаєш\r\nПід землею? Ні, вже, мабуть,\r\nЯ не заховаюсь\r\nІ на небі!.. За що ж кара,\r\nЗа що мені муки?\r\nКому я що заподіяв!\r\nЧиї тяжкі руки\r\nВ тілі душу закували,\r\nСерце запглили\r\nІ галичі силу —\r\nДуми розпустили??\r\nЗа що, не знаю, а караюсь,\r\nІ тяжко караюсь!\r\nІ коли я спокутую,\r\nКоли діжду краю,\r\nНе бачу й не знаю!!\r\n\r\nЗаворушилася пустиня.\r\nМов із тісної домовини\r\nНа той остатній страшний суд\r\nМертвці за правдою встають.\r\nТо не вмерлі, не убиті,\r\nНе суда просити!\r\nНі, то люди, живі люди,\r\nВ кайдани залиті.\r\nІз нор золото виносять,\r\nЩоб пельку залити\r\nНеситому!.. То каторжні.\r\nА за що? т.і знає...\r\nВседержитель... а може, ще\r\nЙ він не добачає.\r\n\r\nОнде злодій штемпований\r\nКайдани волочить;\r\nОн розбойник катований\r\nЗубами скрегоче,\r\nНедобитка товариша\r\nЗарізати хоче!\r\nА меж ними, запеклими,\r\nВ кайдани убраний\r\nЦар всесвітній! цар волі, цар,\r\nШтемпом увінчаний!\r\nВ муці, в каторзі не просить,\r\nНе плаче, не стогне!\r\nРаз добром нагріте серце\r\nВік не прохолоне!\r\n\r\nА де ж твої думи, рожевії квіти,\r\nДоглядані, смілі, викохані діти,\r\nКому ти їх, друже, кому передав?\r\nЧи, може, навіки в серці поховав?\r\nО, не ховай, брате! розсип їх, розкидай!\r\nЗійдуть, і ростимуть, і у люди вийдуть!\r\nЧи ще митарство? чи вже буде?\r\nБуде, буде, бо холодно,\r\nМороз розум будить.\r\n\r\nІ знов лечу. Земля чорніє,\r\nДрімає рсзум, серце мліє.\r\nДивлюся: хати над шляхами\r\nТа городі: з стома церквами,\r\nА в городах, мов журавлі,\r\nЗамуштрували москалі;\r\nНагодовані, обуті\r\nІ кайданачи окуті,\r\nМуштруються... Далі гляну:\r\nУ долині, мов у ямі,\r\nНа багнищі город мріє;\r\nНад ним хмарою чорніє\r\nТуман тяжкий... Долітаю —\r\nТо город безкраїй.\r\nЧи то турецький,\r\nЧи то німецький,\r\nА може, те, що й московський.\r\nЦеркви, та палати,\r\nТа пани пузаті,\r\nІ ні однісінької хати.\r\n\r\nСмеркалося... огонь огнем\r\nКругом запалало,—\r\nАж злякавсь я... «Ура! ура!\r\nУра!» — закричали.\r\n«Цу-цу, дурні! схаменіться!\r\nЧого се ви раді!\r\nЩо горите?» — «Экой хохол!\r\nНе знает параду.\r\nУ нас парад! сам изволит\r\nСегодни гуляти!»\r\n«Та де ж вона, тая цяця?»\r\n«Бон видишь — палаты».\r\nШтовхаюсь я; аж землячок,\r\nСпасибі, признався,\r\nЗ циновими ґудзиками:\r\n«Де ты здесь узялся?»\r\n«З України». — «Так як же ты\r\nЙ говорыть не вмиєш\r\nПо-здешему?» — «Ба ні,— кажу,—\r\nГоворить умію.\r\nТа не хочу».— «Экой чудак!\r\nЯ вси входы знаю,\r\nЯ тут служу; коли хочеш,\r\nВ дворец попитаюсь\r\nВвесты тебе. Только, знаєш,\r\nМы, брат, просвищенны,—\r\nНе поскупись полтинкою...»\r\nЦур тобі, мерзенний\r\nКаламарю... І зробився\r\nЯ знову незримий\r\nТа й пропхався у палати.\r\nБоже мій єдиний!!\r\nТак от де рай! уже нащо\r\nЗолотом облиті\r\nБлюдолизи; аж ось і сам,\r\nВисокий, сердитий,\r\nВиступає; обок його\r\nЦариця небога,\r\nМов опеньок засушений,\r\nТонка, довгонога,\r\nТа ще р;а лихо, сердешне,\r\nХита головою.\r\nТак оце-то та богиня!\r\nЛишенько з тобою.\r\nА я, дурний, не бачивши\r\nТебе, цяце, й разу,\r\nТа й повірив тупорилим\r\nТвоїм в рщомазам.\r\nОто дурний! а ще й битий!\r\nНа каток повірив\r\nМоскалеві. От і читай,\r\nІ йми ти їм віри!\r\nЗа богами — панства, панства\r\nВ серебрі та златі!\r\nМов кабани годовані —\r\nПикаті, пузаті!..\r\nАж потіють, та товпляться,\r\nЩоб то ближче стати\r\nКоло самих: може, вдарять\r\nАбо дулю дати\r\nБлаговонять; хоч маленьку,\r\nХоч півдулі, аби тілько\r\nПід самую пику.\r\nІ всі у ряд поставали,\r\nНіби без\'язикі —\r\nАнітелень. Цар цвенькає;\r\nА диво-цариця,\r\nМов та чапля меж птахами,\r\nСкаче, бадьориться.\r\nДовгенько вдвох походжали\r\nМов сичі надуті,\r\nТа щось нишком розмовляли\r\nЗдалека не чути —\r\nО отечестві, здається,\r\nТа нових петлицях,\r\nТа о муштрах ще новіших!..\r\nА потім цариця\r\nСіла мовчки на дзиґлику.\r\nДивлюсь, цар підходить\r\nДо найстаршого... та в пику\r\nЙого як затопить!..\r\nОблизався неборака\r\nТа меншого в пузо —\r\nАж загуло!.. а той собі\r\nЩе меншого туза\r\nМежи плечі; той меншого,\r\nА менший малого,\r\nА той дрібних, а дрібнота\r\nУже за порогом\r\nЯк кинеться по улицях,\r\nТа й дівай місити\r\nНедобитків православних,\r\nА ті голосити;\r\nТа верещать; та як ревнуть:\r\n«Гуля наш батюшка, ґуля!\r\nУра!..ура!..ура! а, а, а...»\r\nЗареготався я, та й годі;\r\nА й мене давнули\r\nТаки добре. Перед світом\r\nУсе те заснуло;\r\nТільки де-де православні\r\nПо углах стогнали\r\nТа, стогнучи, за батюшку\r\nГоспода благали.\r\nСміх і сльози! От пішов я\r\nГород озирати.\r\nТам ніч, як день. Дивлюся:\r\nПалати, палати\r\nПонад тихою рікою;\r\nА беріг ушитий\r\nУвесь каменем. Дивуюсь,\r\nМов несамовитий!\r\nЯк то ноно зробилося\r\nЗ калюжі такої\r\nТаке диво... отут крові\r\nПролито людської —\r\nІ без ножа. По тім боці\r\nТвердиня й дзвіниця,\r\nМов та швайка загострена,\r\nАж чудно дивиться.\r\nІ дзиггрі теленькають.\r\nОт я повертаюсь —\r\nАж кінь летить, копитами\r\nСкелю розбиває!\r\nА на коні сидить охляп,\r\nУ свит — не свиті,\r\nІ без шапки. Якимсь листом\r\nГолова повита.\r\nКінь басує, от-от річку,\r\nОт... от... перескочить.\r\nА він руку простягає,\r\nМов світ увесь хоче\r\nЗагарбати. Хто ж це такий?\r\nОт собі й читаю,\r\nЩо на скелі наковано:\r\nПервому— Вторая\r\nТаке диво наставила.\r\nТепер же я знаю:\r\nЦе той   П е р в и й   що розпинав\r\nНашу Україну,\r\nА   В т о р а я   доконала\r\nВдову сиротину.\r\nКати! кати! людоїди!\r\nНаїлись обоє,\r\nНакралися; а що взяли\r\nНа той світ з собою?\r\nТяжко, тяжко мені стало,\r\nТак, мов я читаю\r\nІсторія України.\r\nСтою, замираю...\r\nА тим часом — тихо, тихо\r\nТа сумно співає\r\nЩось такеє невидиме:\r\n\r\n«Із города із Глухова\r\nПолки виступали\r\nЗ заступами на лінію,\r\nА мене послали\r\nНа столицю з козаками\r\nНаказним гетьманом!\r\nО боже наш милосердий!\r\nО царю поганий,\r\nЦарю проклятий, лукавий,\r\nАспиде неситий!\r\nЩо ти зробив з козаками?\r\nБолота засипав\r\nБлагородними костями;\r\nПоставив столицю\r\nНа їх трупах катованих!\r\nІ в темній темниці\r\nМене, вольного гетьмана,\r\nГолодом замучив\r\nУ кайданах. Царю! царю!\r\nІ бог не розлучить\r\nНас з тобою. Кайданами\r\nСкованій зо мною\r\nНавік-віки. Тяжко мені\r\nВитать над Невою.\r\nУкраїни далекої,\r\nМоже, вже немає.\r\nПолетів би, подивився,\r\nТак бог не пускає.\r\nМоже, Москва випалила\r\nІ Дніпро спустила\r\nВ синє море, розкопала\r\nВисокі могили —\r\nНашу славу. Боже милий,\r\nЗжалься, боже милий».\r\nТа й замовкло; дивлюся я:\r\nБіла хмара криє\r\nСіре небо. А в тій хмарі\r\nМов звір в гаї виє.\r\nТо не хмара — біла пташка\r\nХмарою спустилась\r\nНад царем тим мусянджовим\r\nІ заголосила:\r\n«І ми сковані з тобою,\r\nЛюдоїде, змію!\r\nНа страішному на судищі\r\nМи бог і закриєм\r\nОд очей твоїх неситих.\r\nТи нас з України\r\nЗагнав, голих і голодних,\r\nУ сніг на чужину\r\nТа й порізав; а з шкур наших\r\nСобі багряницю\r\nПошив жилами твердими\r\nІ заклав; столицю\r\nВ новій рясі. Подивися:\r\nЦеркви та палати!\r\nВеселися, лютий кате,\r\nПроклятий! проклятий!»\r\n\r\nРозлетілись, розсипались,\r\nСонечко вставало.\r\nА я стояв, дивувався,\r\nТа аж страшно стало.\r\nУже вбогі ворушились,\r\nНа труд поспішали,\r\nІ москалі на розпуттях\r\nУже муштрувались.\r\nПокрай улиць поспішали\r\nЗаспані дівчата,\r\nТа не з дому, а додому!\r\nПосилала мати\r\nНа цілу ніч працювати,\r\nНа хліб заробляти.\r\nА я стою, похилившись,\r\nДумаю, гадаю,\r\nЯк то тяжко той насущний\r\nЛюди заробляють.\r\nОт і братія сипнула\r\nУ сенат писати\r\nТа підписувать — та драти\r\nІ з батька, і брата.\r\nА меж ними і землячки\r\nДе-де проглядають.\r\nПо-московській так і ріжуть,\r\nСміються та лають\r\nБатьків своїх, що змалечку\r\nЦвенькать не навчили\r\nПо-німецькій,— а то тепер\r\nІ кисни в чорнилах!\r\nП\\\'явки! п\\\'явки! може, батько\r\nОстатню корову\r\nЖидам продав, поки вивчив\r\nМосковської мови.\r\nУкраїно! Україно!\r\nОце твої діти,\r\nТвої квіти молодії,\r\nЧорнилом политі,\r\nМосковською блекотою\r\nВ німецьких теплицях\r\nЗаглушені!.. Плач, Украйно!\r\nБездітна вдовице!\r\n\r\nПіти лишень подивиться\r\nДо царя в палати,\r\nЩо там робиться. Приходжу:\r\nСтаршина пузата\r\nСтоїть рядом; сопе, хропе,\r\nТа понадувалось,\r\nЯк індики, і на двері\r\nКосо поглядало.\r\nАж ось вони й одчинились.\r\nНеначе з берлоги\r\nМедвідь виліз, ледве-ледве\r\nПереносить ноги;\r\nТа одутий, аж посинів:\r\nПохмілля прокляте\r\nЙого мучило. Як крикне\r\nНа самих пузатих —\r\nВсі пузаті до одного\r\nВ землю провалились!\r\nВін вилупив баньки з лоба —\r\nІ все затрусилось,\r\nЩо осталось; мов скажений,\r\nНа менших гукає —\r\nІ ті в землю; він до дрібних\r\nІ ті пропадають!\r\nВін до челяді — і челядь,\r\nІ челядь пропала;\r\nДо москалів — москалики,\r\nТілько застогнало,\r\nПішли в землю; диво дивне\r\nСталося на світі.\r\nДивлюся я, що дальш буде,\r\nЩо буде робити\r\nМій медведик! Стоїть собі,\r\nГолову понурив\r\nСіромаха. Де ж ділася\r\nМедвежа натура?\r\nМов кошеня, такий чудний.\r\nЯ аж зісміявся.\r\nВін і почув, та як зикне,—\r\nЯ перелякався,\r\nТа й прокинувсь... Отаке-то\r\nПриснилося диво.\r\nЧудне якесь!.. таке тілько\r\nСниться юродивим\r\nТа п\\\'яницям. Не здивуйте,\r\nБрати любі, милі,\r\nЩо не своє розказав вам,\r\nА те, що приснилось.', 1),
(11, 'Серце мліло, не хотіло<br>Cпівать на чужині...', 'Не хотілось в снігу, в лісі,\r\nКозацьку громаду\r\nЗ булавами, з бунчугами\r\nЗбирать на пораду.\r\nНехай душі козацькії\r\nВ Украйні витають –\r\nТам широко, там весело\r\nОд краю до краю...\r\nЯк та воля, що минулась,\r\nДніпр широкий – море,\r\nСтеп і степ, ревуть пороги,\r\nІ могили – гори, –\r\nТам родилась, гарцювала\r\nКозацькая воля;\r\nТам шляхтою, татарами\r\nЗасідала поле,\r\nЗасівала трупом поле,\r\nПоки не остило...', 1),
(12, 'Спомин', 'У садочку в холодочку\r\nГарна мати молода\r\nВозить донечку в візочку,\r\nВозить звільна і гойда.\r\nМірно, наче пташка в клітці\r\nЗ щеблика на щеблик скаче,\r\nВозить доню в холодочку,\r\nХоч дитина спить, не плаче.\r\nДо візка не заглядають\r\nМатеринські очі чулі,\r\nЛиш уста тихенько шепчуть:\r\nЛюлі-люлі, люлі-люлі.', 6),
(13, 'Червона Калино', 'Червона калино,\r\nЧого в лузі гнешся?\r\nЧи світла не любиш — \r\nДо сонця не пнешся?\r\n\r\nНе жаль мені цвіту, \r\nНе страшно і грому, \r\nІ світло люблю я. \r\nКупаюся в ньому.\r\n \r\nЧи жаль тобі цвіту, \r\nЧи радощів світу? \r\nЧи бурі боїшся,\r\nЧи грому з блакиту?\r\n\r\nА вгору не пнуся. \r\nБо сили не маю, \r\nЧервоні ягідки \r\nДодолу схиляю.\r\n\r\nЯ вгору не пнуся,\r\nЯ дубам не пара,\r\nА ти ж мене, дубе. \r\nОтінив, мов хмара.', 6),
(14, 'У долині село лежить', 'У долині село лежить, \r\nпонад селом туман дрижить, \r\nа на горбі край села \r\nстоїть кузня немала.\r\n\r\nА в тій кузні коваль клепле, \r\nа в коваля серце тепле, \r\nа він клепле та й співа, \r\nвсіх до кузні іззива.\r\n\r\n«Ходіть, люди, з хат, із поля! \r\nТут кується краща доля.\r\nХодіть, люди, порану, \r\nвибивайтесь з туману!»\r\n\r\nТа тумани хитаються, \r\nпонад селом згущаються, \r\nрозляглися по полях, \r\nщоб затьмити людям шлях.\r\n\r\nЩоб закрити їм стежини \r\nті, що вгору йдуть з долини, \r\nв тую кузню, де кують \r\nясну зброю замість пут.', 6);

